    private void bCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bCalcularActionPerformed
       double valor, total,desconto,valorPagar;
       int quant;
       
       valor = Double.parseDouble(tfValor.getText());
       quant = Integer.parseInt(tfQuant.getText());
       
       total = valor*quant;
       tfTotal.setText(String.valueOf(total));
       desconto = Double.parseDouble(tfDesconto.getText());
       valorPagar = total - total * desconto;
       
       lValor.setText(String.valueOf(valorPagar));
       
    }//GEN-LAST:event_bCalcularActionPerformed

